<script lang="ts">
    import {coupon} from "../coupon.svelte";

    interface list {
        _id : string;
        name: string;
        userId: string;
        timestamp: string;
        selected: boolean;
    }

    interface sendCoupon {
        name: string;
        details: string;
        startDate: string;
        endDate: string;
        customers: list[];
    }

    let checkedAll: boolean = $state(false);

    let customers: list[] = $state([]);

    async function fetchCustomers() {
        const response = await fetch("https://temp-back-phycom-proj.vercel.app/details");
        const data = await response.json();
        customers = data.map( (customer: { _id: string; name: string; userId: string; timestamp: string; }) => ({
            ...customer,
            selected: false
        }));
    }

    function toggleCheckbox(index: number) {
        customers[index].selected = !customers[index].selected;
    }

    function selectAll() {
        checkedAll = !checkedAll;
        customers = customers.map(customer => ({ ...customer, selected: checkedAll }));
    }

    async function sendSelectedCustomers() {
      const selectedCustomers = customers.filter(customer => customer.selected);
      const couponData: sendCoupon = {
          name: coupon.name,
          details: coupon.details,
          startDate: coupon.startDate,
          endDate: coupon.endDate,
          customers: selectedCustomers
      };

      console.log("Sending coupon data:", couponData);

      await fetch("https://temp-back-phycom-proj.vercel.app/send-coupon", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(couponData)
      });

      alert("send coupon successfully");
      // ✅ Reset coupon store
      coupon.name = "";
      coupon.details = "";
      coupon.startDate = "";
      coupon.endDate = "";
    }
    // $inspect(customers);

</script>

<div class="bg-white px-6 pt-5 pb-4 rounded-lg shadow-lg max-w-md w-[550px] mx-1 relative z-10">
    <div class="border-b border-gray-200 pb-1">
        <h2 class="font-semibold text-gray-800 flex justify-between items-center">
        <span>รายชื่อลูกค้า</span>
        <span>เลือก</span>
        </h2>
    </div>

    <div class="scrollbar-thin h-96 overflow-y-auto max-w-md">
        {#each customers as customer, index (index)}
        <div class="hover:bg-gray-50">
            <div class="flex items-center justify-between py-2 border-b border-gray-100 last:border-b-0">
            <div class="flex items-center space-x-3">
                <img
                src="data:image/png;base64,"
                alt="Avatar"
                class="w-10 h-10 rounded-full shrink-0 border-gray-200 border-2"/>
                <div class="flex flex-col ml-3">
                <p class="font-medium text-gray-800">{customer.name}</p>
                </div>
            </div>
            <input type="checkbox" checked={customer.selected} class="form-checkbox h-5 w-5 text-blue-600 rounded" onclick={() => toggleCheckbox(index)}/>
            </div>
        </div>
        {/each}

    </div>

    <div class="px-2 pt-2 flex items-center justify-end space-x-4">
    <label class="flex items-center space-x-2 text-gray-700">
      <span>เลือกทั้งหมด</span>
      <input type="checkbox" checked={checkedAll} class="form-checkbox h-5 w-5 text-blue-600 rounded" onclick={() => selectAll()} />
    </label>
    <button onclick={() => sendSelectedCustomers()}
      class="
        px-6
        bg-green-500
        text-white
        font-semibold
        rounded-full
        shadow-md
        hover:bg-green-600
        focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2
        transition-all
      "
    >ส่ง</button>
    <button
      class="
        px-6
        bg-blue-500
        text-white
        font-semibold
        rounded-full
        shadow-md
        hover:bg-blue-600
        focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2
        transition-all
      "
      onclick={() => fetchCustomers()}
    >
      รีเฟรช
    </button>
  </div>

</div>